const getLastWorks=()=>{$.ajax({type:"GET",url:"https://www.centercollorvilanova.com.br/recent_works",dataType:"JSON",success:a=>{(a=>{let s="";(a.status=1)&&(a.data.forEach(a=>{s+=`\n                    <div class="col-md-4">\n                        <div class="project" style="background-image: url(${a.image});">\n                            <div class="desc">\n                                <div class="con">\n                                    <h3><a href="${a.link}">${a.client}</a></h3>\n                                    <span>${a.description}</span>\n                                    <br>\n                                    <p class="icon">\n                                        <span><a href="${a.link}" target="_blank"><i class="fa fa-share"></i></a></span>\n                                        ${"App"==a.class?'<span><a href="#"><i class="fa fa-mobile"></i></a></span>':"Sistema"==a.class?'<span><a href="#"><i class="fa fa-code"></i></a></span>':'<span><a href="#"><i class="fa fa-desktop"></i></a></span>'}\n                                        \n                                        <br>\n                                        <span><a href="#"><small>${a.tags}</small></a></span>\n                                        \n                                        <br>\n                                        <span><a href="#"><small>${a.date_deploy}</small></a></span>\n                                    </p>\n                                </div>\n                            </div>\n                        </div>\n                    </div>\n                `}),$(".work-content").html(s).hide().toggle("slide"))})(a)},beforesend:a=>{$(".work-loading").css("display","block")},error:a=>{$(".work-loading").css("display","none")},complete:a=>{$(".work-loading").css("display","none")}})};$(function(){getLastWorks()});